define(['jquery', 'underscore', 'oae/api/oae.core'], function($, _, oae) {

    return function(uid, showSettings) {

        var $rootel = $('#' + uid);

        var loadPage = function(ev) {
            $('#oae-left-column').find('ul li').removeClass('selected');
            $(ev.target).parents('li').addClass('selected');
            var widgetsToLoad = $(ev.target).attr('data-widgets');
            $(window).trigger('init.widget.sakai', widgetsToLoad);
        };

        var addBinding = function() {
            $(document).on('click', '.lhnavigation_link', loadPage);
        };

        var doInit = function() {
            // Add binding to the navigation
            addBinding();

            // Render the navigation
            oae.api.util.renderTemplate('#lhnavigation-firstcolumn-template', null, $('#oae-left-column'));

            // TODO: Replace with better logic for selecting a page taking into account the hash
            // select the first page
            $('#oae-left-column').find('ul li a')[0].click();
        };

        doInit();

    };

});
